import { useState } from 'react';
import B from './b';

function A() {
  const [cendol, setCendol] = useState(2);

  const handleIncrement = () => {
    setCendol(cendol + 10);
  }

  const [count, setCount] = useState(0);
  const [bgColor, setBgColor] = useState('');
  const [isTrue, setIsTrue] = useState(false);

  const handleClick = () => {
    setCount(count + 1);
  }
  

  const handleChange = () => {
    setIsTrue(!isTrue);
  }

  return (
    <div>
            <div className="circle">{count}</div>
      <button onClick={handleClick} style={{ backgroundColor: bgColor }}>Increment</button>
      <button onClick={handleChange} style={{ backgroundColor: isTrue ? 'red' : 'blue' }}>
        Click me
      </button>
      <p>Cendol: {cendol}</p>
      <button onClick={handleIncrement}>Increment</button>
      <B cendol={cendol} />
    </div>
  );
}

export default A;
