import './App.css';
import { useState } from 'react';

function App() {
  const [count, setCount] = useState(0);
  const [bgColor, setBgColor] = useState('');
  const [isTrue, setIsTrue] = useState(false);

  const handleClick = () => {
    setCount(count + 1);
    setBgColor('red');
  }


  const handleChange = () => {
    setIsTrue(!isTrue);
  }
  return (
    <div className="App">
      <div className="circle">{count}</div>
      <button onClick={handleClick} style={{ backgroundColor: bgColor }}>Increment</button>
      <button onClick={handleChange} style={{ backgroundColor: isTrue ? 'red' : 'blue' }}>
        Click me
      </button>
    </div>
  );
}
export default App;
