import './App.css';
import { useState } from 'react';

function App() {
  const [count, setCount] = useState(0);
  const [bgColor, setBgColor] = useState('');

  const handleClick = () => {
    setCount(count + 1);
    setBgColor('red');
  }
  return (
    <div className="App">

      <button onClick={handleClick} style={{ backgroundColor: bgColor }}>
      </button>
    </div>
  );
}
export default App;
